import React, { useState } from "react";
import "./StudentProfile.css";

 function StudentProfile() {
  const [profile, setProfile] = useState({
    name: "",
    email: "",
    study: "",
    field: "",
    profilePic: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile({ ...profile, profilePic: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Profile Saved:", profile);
    alert("Profile Saved Successfully!");
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2 className="title">Student Profile</h2>

        <form onSubmit={handleSubmit}>
          {/* Profile Picture */}
          <div className="profile-pic-wrapper">
            <label htmlFor="profilePic" className="profile-pic-label">
              {profile.profilePic ? (
                <img
                  src={profile.profilePic}
                  alt="Profile"
                  className="profile-pic"
                />
              ) : (
                <div className="profile-pic-placeholder">Upload</div>
              )}
            </label>
            <input
              type="file"
              id="profilePic"
              accept="image/*"
              onChange={handleImageChange}
              className="hidden-input"
            />
          </div>

          {/* Input Fields */}
          <input
            type="text"
            name="name"
            value={profile.name}
            onChange={handleChange}
            placeholder="Full Name"
            required
          />

          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
            placeholder="Email"
            required
          />

          <input
            type="text"
            name="study"
            value={profile.study}
            onChange={handleChange}
            placeholder="What do you study? (e.g. Computer Science)"
            required
          />

          <input
            type="text"
            name="field"
            value={profile.field}
            onChange={handleChange}
            placeholder="Field/Department (e.g. Engineering)"
            required
          />

          <button type="submit" className="save-btn">
            Save Profile
          </button>
        </form>
      </div>
    </div>
  );
}

export default StudentProfile;
