// import React from "react";
import "./Choose.css";
// import { useNavigate } from "react-router-dom";
import { FaGraduationCap, FaChalkboardTeacher, FaUserCog } from 'react-icons/fa';
// import { usePortal } from './PortalContext';

const PortalPage = () => {
    // const Navig = useNavigate();

    const portals = [
        {
            type: 'student',
            title: 'Student Portal',
            description: 'Access your courses, assignments, and grades in one place. Stay on track with your academic journey.',
            icon: <FaGraduationCap size={40} />,
            path: '/login'
        },
        {
            type: 'faculty',
            title: 'Faculty Portal',
            description: 'Manage courses, upload materials, and track student progress. Create an engaging learning environment.',
            icon: <FaChalkboardTeacher size={40} />,
            path: '/login'
        },
        {
            type: 'admin',
            title: 'Admin Portal',
            description: 'Manage users, monitor activities, and keep the system running smoothly with powerful tools.',
            icon: <FaUserCog size={40} />,
            path: '/login'
        }
    ];


    return (
        <div className="portal-page">
            <div className="portal-container">
                {portals.map((portal, index) => (
                    <div 
                        key={index} 
                        className={`portal-card portal-card--${portal.type}`}
                    >
                        <div className="portal-card__icon">
                            {portal.icon}
                        </div>
                        <h2 className="portal-card__title">
                            {portal.title}
                        </h2>
                        <p className="portal-card__description">
                            {portal.description}
                        </p>
                        <button 
                            className="portal-btn" onClick={handle_click(index)}
                            >
                            Enter Portal
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PortalPage;
